import React, { useEffect, useState } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

const RecipeList = () => {

	const [recipes, setRecipes] = useState([]);
	const [loading, setLoading] = useState(false);

	useEffect(() => {
		setLoading(true);

		fetch('dishout/recipes')
			.then(response => response.json())
			.then(data => {
				setRecipes(data);
				setLoading(false);
			})
	}, []);

	const remove = async (id) => {
		await fetch(`/dishout/recipe/${id}`, {
			method: 'DELETE',
			headers: {
				'Accept': 'application/json',
				'Content-Type': 'application/json'
			}
		}).then(() => {
			let updatedRecipes = [...recipes].filter(i => i.id !== id);
			setRecipes(updatedRecipes);
		});
	}

	if (loading) {
		return <p>Loading...</p>;
	}

	const recipeList = recipes.map(recipe => {
		return <tr key={recipe.id}>
			<td style={{whiteSpace: 'nowrap'}}>{recipe.title}</td>
			<td>{recipe.tags.map(tag => {
				return {tag}
			})}</td>
			<td>
				<ButtonGroup>
					<Button size="sm" color="primary" tag={Link} to={"/recipes/" + recipe.id}>Edit</Button>
					<Button size="sm" color="danger" onClick={() => remove(recipe.id)}>Delete</Button>
				</ButtonGroup>
			</td>
		</tr>
	});

	return (
		<div>
			<AppNavbar/>
			<Container fluid>
				<div className="float-end">
					<Button color="success" tag={Link} to="/recipes/new">Add Recipe</Button>
				</div>
				<h3>My Recipes</h3>
				<Table className="mt-4">
					<thead>
					<tr>
						<th width="20%">Title</th>
						<th>Tags</th>
						<th width="20%">Actions</th>
					</tr>
					</thead>
					<tbody>
					{recipeList}
					</tbody>
				</Table>
			</Container>
		</div>
	);
};

export default RecipeList;
