import React, { useEffect, useState } from "react";
import './App.css';

const App = () => {

	const [recipes, setRecipes] = useState([]);
	const [loading, setLoading] = useState(false);

	useEffect(() => {
		setLoading(true);

		fetch('dishout/recipes')
			.then(response => response.json())
			.then(data => {
				setRecipes(data);
				setLoading(false);
			})
	}, []);

	if (loading) {
		return <p>Loading...</p>;
	}

	return (
		<div className="App">
			<header className="App-header">
				<div className="App-intro">
					<h2>Dish-Out</h2>
					{recipes.map(recipe =>
						<div key={recipe.id}>
							{recipe.title}
							<br /><br />
							<h1>Ingredients: </h1>
							{recipe.ingredients}
							<br /><br />
							<h1>Directions: </h1>
							{recipe.directions}
						</div>
					)}
				</div>
			</header>
		</div>
	);
}

export default App;
